mapper.select:
	SELECT ASI.*,
	 	P3.NOMBRE_PARAMETRO AS NOMBRE_NIVEL_JERARQUICO,
		P2.NOMBRE_PARAMETRO AS  NOMBRE_GRADO
	 FROM ASIGNACION_SALARIAL ASI
    	LEFT JOIN PARAMETRICA P2 on ASI.COD_GRADO = P2.COD_TABLA_PARAMETRICA
    	LEFT JOIN PARAMETRICA P3 on ASI.COD_NIVEL_JERARQUICO = P3.COD_TABLA_PARAMETRICA
	WHERE 1=1

        AND ASI.COD_ASIGNACION_SALARIAL = #{codAsignacionSalarial,jdbcType=NUMERIC}


        AND ASI.COD_NIVEL_JERARQUICO = #{codNivelJerarquico,jdbcType=NUMERIC}


        AND ASI.COD_GRADO = #{codGrado,jdbcType=NUMERIC}


        AND ASI.ASIGNACION_BASICA = #{asignacionBasica,jdbcType=NUMERIC}


        AND ASI.OTRAS_ASIGNACIONES = #{otrasAsignaciones,jdbcType=NUMERIC}


        AND ASI.VALOR_ASIGNACION = #{valorAsignacion,jdbcType=NUMERIC}


        AND ASI.PORCENTAJE_ASIGNACION = #{porcentajeAsignacion,jdbcType=NUMERIC}


        AND ASI.AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


        AND ASI.AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


        AND ASI.AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


        AND ASI.AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


        AND ASI.COD_CARGO = #{codCargo,jdbcType=NUMERIC}


        AND ASI.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


        AND ASI.COD_NOMENCLATURA_DENOMINACION = #{codNomenclaturaDenominacion,jdbcType=NUMERIC}
