mapper.select:
select 
count(*) over() TOTAL,

from AUDITORIA_CONFIGURACION AC
WHERE 1 = 1

    AND UPPER(NOMBRE_TABLA) LIKE UPPER('%'||#{nombreTabla,jdbcType=VARCHAR}||'%')


    AND UPPER(DESCRIPCION_TABLA) LIKE UPPER('%'||#{descripcionTabla,jdbcType=VARCHAR}||'%')


    AND FLG_INSERT = #{flgInsert,jdbcType=NUMERIC}


    AND FLG_DELETE = #{flgDelete,jdbcType=NUMERIC}


    AND FLG_UPDATE = #{flgUpdate,jdbcType=NUMERIC}


    AND FLG_SELECT = #{flgSelect,jdbcType=NUMERIC}


    FECHA_INICIO_AUDITORIA = #{fechaInicioAuditoria,jdbcType=TIMESTAMP}


    AND FECHA_FIN_AUDITORIA = #{fechaFinAuditoria,jdbcType=TIMESTAMP}


    AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


    AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


    AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


    AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


   AND UPPER(NOMBRE_FUNCIONAL) LIKE UPPER('%'||#{nombreFuncional,jdbcType=VARCHAR}||'%') 

ORDER BY NOMBRE_TABLA
