mapper.select:
   select c.*,
		CONCAT(
		    CONCAT(
		    CONCAT((SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = c.cod_grado),' '),
		    CONCAT((SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = c.cod_nivel_cargo),' ')
		    ),c.nombre_cargo
		) as NOMBRE_CARGO
		from CARGO c
		inner join ENTIDAD_CARGO ce on ce.COD_ENTIDAD_CARGO = c.COD_CARGO
		where ce.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}

    select 
       count(*) over() TOTAL,
		C.*,
		(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = C.COD_GRADO) AS GRADO_NOMBRE
		from cargo C
		where 1 = 1

         AND COD_CARGO =  #{codCargo,jdbcType=NUMERIC}


        AND CODIGO_DAFP = #{codigoDafp,jdbcType=NUMERIC}


        AND NOMBRE_CARGO =  #{nombreCargo,jdbcType=VARCHAR}


        AND ASIGNACION_SALARIAL = #{asignacionSalarial,jdbcType=NUMERIC}


        AND NOMBRE_DENOMINACION = #{nombreDenominacion,jdbcType=NUMERIC}


        AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


          OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

SELECT * FROM CARGO CAR 
       INNER JOIN ENTIDAD_CARGO EC ON CAR.COD_CARGO = EC.COD_CARGO 
        WHERE EC.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}

        	AND CAR.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
