mapper.select:
select

from CM_CREAR_GRUPO
WHERE 1 = 1

    AND COD_DEPENDENCIA_ENTIDAD = #{codDependenciaEntidad,jdbcType=VARCHAR}


    AND COD_GRUPO = #{codGrupo,jdbcType=VARCHAR}


    AND NOMBRE_GRUPO_DEPENDENCIA = #{nombreGrupoDependencia,jdbcType=VARCHAR}


    AND COD_DEPARTAMENTO = #{codDepartamento,jdbcType=VARCHAR}


    AND COD_MUNICIPIO = #{codMunicipio,jdbcType=VARCHAR}


    AND EMAIL_ENTIDAD = #{emailEntidad,jdbcType=VARCHAR}


    AND LATITUD = #{latitud,jdbcType=VARCHAR}


    AND LONGITUD = #{longitud,jdbcType=VARCHAR}


    AND PAGINA_WEB = #{paginaWeb,jdbcType=VARCHAR}


    AND TIPO_ZONA = #{tipoZona,jdbcType=VARCHAR}


    AND DIRECCION = #{direccion,jdbcType=VARCHAR}


    AND CODIGO_POSTAL = #{codigoPostal,jdbcType=VARCHAR}


    AND TELEFONO_ENTIDAD = #{telefonoEntidad,jdbcType=VARCHAR}


    AND CELULAR = #{celular,jdbcType=VARCHAR}


    AND FAX = #{fax,jdbcType=VARCHAR}


    AND SIGLA = #{sigla,jdbcType=VARCHAR}


    AND CODIGO_DANE = #{codigoDane,jdbcType=VARCHAR}


    AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


    AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


    AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


    AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


    AND COD_PROCESO_CARGA_MASIVA = #{codProcesoCargaMasiva,jdbcType=NUMERIC}


    AND RESULTADO = #{resultado,jdbcType=VARCHAR}
