mapper.select:
  select

  from CM_CREAR_PLANTA
		where 1 = 1

      AND COD_CLASE_PLANTA = #{codClasePlanta,jdbcType=VARCHAR}


      AND COD_CLASIFICACION_PLANTA = #{codClasificacionPlanta,jdbcType=VARCHAR}


      AND COD_TIPO_NORMA = #{codTipoNorma,jdbcType=VARCHAR}


      AND FECHA_NORMA = #{fechaNorma,jdbcType=VARCHAR}


      AND NUM_NORMA = #{numNorma,jdbcType=VARCHAR}


      AND FECHA_INICIO = #{fechaInicio,jdbcType=VARCHAR}


      AND FECHA_FIN = #{fechaFin,jdbcType=VARCHAR}


      AND COD_NOMENCLATURA = #{codNomenclatura,jdbcType=VARCHAR}


      AND SISTEMA_CARRERA = #{sistemaCarrera,jdbcType=VARCHAR}


      AND COD_NIVEL = #{codNivel,jdbcType=VARCHAR}


      AND COD_DENOMINACION = #{codDenominacion,jdbcType=VARCHAR}


      AND CODIGO = #{codigo,jdbcType=VARCHAR}


      AND GRADO = #{grado,jdbcType=VARCHAR}


      AND COD_TIPO_PLANTA = #{codTipoPlanta,jdbcType=VARCHAR}


      AND COD_DEPENDENCIA = #{codDependencia,jdbcType=VARCHAR}


      AND CANTIDAD_CARGOS = #{cantidadCargos,jdbcType=VARCHAR}


      AND CANTIDAD_CARGOS_AP_PPTAL = #{cantidadCargosApPptal,jdbcType=VARCHAR}


      AND GERENCIA_PUBLICA = #{gerenciaPublica,jdbcType=VARCHAR}


      AND NATURALEZA_EMPLEO = #{naturalezaEmpleo,jdbcType=VARCHAR}


      AND PEP = #{pep,jdbcType=VARCHAR}


      AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


      AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


      AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


      AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


      AND COD_PROCESO_CARGA_MASIVA = #{codProcesoCargaMasiva,jdbcType=NUMERIC}


      AND RESULTADO = #{resultado,jdbcType=VARCHAR}


      AND NOMBRE_PLANTA = #{nombrePlanta,jdbcType=VARCHAR}
