mapper.select:
select (SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = C.COD_MONEDA_MONTO) NOMBRE_MONEDA, (SELECT PRIMER_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_NOMBRE, (SELECT SEGUNDO_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_NOMBRE, (SELECT PRIMER_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_APELLIDO, (SELECT SEGUNDO_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_APELLIDO, E.NOMBRE_ENTIDAD, C.*, case when (select max(((mc.FECHA_FIN_MODIFICACION))) from modificacion_contrato mc where C.cod_contrato = mc.cod_contrato and mc.COD_TIPO_MODIFICACION_CONTRATO = 884 and mc.COD_MODIFICACION_CONTRATO = (select max (z.COD_MODIFICACION_CONTRATO) from modificacion_contrato z where z.COD_TIPO_MODIFICACION_CONTRATO in (884,1001) and z.COD_CONTRATO = mc.cod_contrato)
)>= TO_DATE(TO_CHAR(CURRENT_TIMESTAMP, 'ddMMyyyy'), 'ddMMyyyy') then 1 else 0 end as FLG_ESTADO_SUSPENDIDO, case when (select max(((mc.FECHA_FIN_MODIFICACION))) from modificacion_contrato mc where C.cod_contrato = mc.cod_contrato and mc.COD_TIPO_MODIFICACION_CONTRATO = 882 and mc.flg_activo = 1 )>= TO_DATE(TO_CHAR(CURRENT_TIMESTAMP, 'ddMMyyyy'), 'ddMMyyyy') then 1 else 0 end as FLG_ESTADO_PRORROGA from CONTRATO C, ENTIDAD E WHERE C.COD_ENTIDAD = E.COD_ENTIDAD and C.COD_PERSONA = #{codPersona,jdbcType=NUMERIC} AND C.FLG_ELIMINADO = 0
and C.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
ORDER BY COD_CONTRATO DESC

select (SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = C.COD_MONEDA_MONTO) NOMBRE_MONEDA, (SELECT PRIMER_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_NOMBRE, (SELECT SEGUNDO_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_NOMBRE, (SELECT PRIMER_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_APELLIDO, (SELECT SEGUNDO_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_APELLIDO, C.* from CONTRATO C where C.FECHA_FIN <= #{fechaFin,jdbcType=TIMESTAMP}
and C.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
and C.COD_PERSONA = #{codPersona,jdbcType=NUMERIC}
and C.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
ORDER BY COD_CONTRATO DESC OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY


select
(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = C.COD_MONEDA_MONTO) NOMBRE_MONEDA,
(SELECT PRIMER_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_NOMBRE,
(SELECT SEGUNDO_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_NOMBRE,
(SELECT PRIMER_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_APELLIDO,
(SELECT SEGUNDO_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_APELLIDO,
C.*
from CONTRATO C
 WHERE 1=1


  	and (trunc(to_date(sysdate,'DD/MM/YYYY')) - trunc(to_date(C.FECHA_FIN,'DD/MM/YYYY')))* -1  BETWEEN #{diasIni,jdbcType=NUMERIC} AND #{diasFin,jdbcType=NUMERIC}


  	and (trunc(to_date(sysdate,'DD/MM/YYYY')) - trunc(to_date(C.FECHA_FIN,'DD/MM/YYYY')))  BETWEEN #{diasIni,jdbcType=NUMERIC} AND #{diasFin,jdbcType=NUMERIC}


  	and C.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC} 


  	and C.COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


  	and C.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}

ORDER BY COD_CONTRATO DESC
OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY


select
(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = C.COD_MONEDA_MONTO) NOMBRE_MONEDA,
(SELECT PRIMER_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_NOMBRE,
(SELECT SEGUNDO_NOMBRE FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_NOMBRE,
(SELECT PRIMER_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) PRIMER_APELLIDO,
(SELECT SEGUNDO_APELLIDO FROM PERSONA WHERE COD_PERSONA = C.COD_PERSONA) SEGUNDO_APELLIDO,
C.*
from CONTRATO C
 where C.COD_PERSONA > 0

  and C.COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


    and C.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


    and C.COD_DEPENDENCIA_ENTIDAD = #{codDependenciaEntidad ,jdbcType=NUMERIC}


    and C.COD_GRUPO_DEPENDENCIA = #{codGrupoDependencia,jdbcType=NUMERIC}


    and C.COD_DEPARTAMENTO = #{codDepartamento,jdbcType=NUMERIC}


    COD_MUNICIPIO = #{codMunicipio,jdbcType=NUMERIC}


    and C.FECHA_FIRMA = #{fechaFirma,jdbcType=TIMESTAMP}


    and C.FECHA_INICIO = #{fechaInicio,jdbcType=TIMESTAMP}


    and C.FECHA_FIN = #{fechaFin,jdbcType=TIMESTAMP}


    and C.NUMERO_CONTRATO = #{numeroContrato,jdbcType=VARCHAR}


    and C.DURACION_CONTRATO = #{duracionContrato,jdbcType=NUMERIC}


    and UPPER(C.OBJETO_CONTRATO) like '%'||UPPER(#{objetoContrato,jdbcType=VARCHAR})||'%'


    and C.ALCANCE_CONTRATO = #{alcanceContrato,jdbcType=VARCHAR}


    and C.COD_MONEDA_MONTO = #{codMonedaMonto,jdbcType=NUMERIC}


    and C.MONTO = #{monto,jdbcType=NUMERIC}


    and C.HONORARIOS = #{honorarios,jdbcType=NUMERIC}


    and C.COD_FUENTE_FINANCIACION = #{codFuenteFinanciacion,jdbcType=NUMERIC}


    and C.COD_ADMINISTRACION_RECURSO = #{codAdministracionRecurso,jdbcType=NUMERIC}


    and C.DERECHO_EXCLUSIVIDAD = #{derechoExclusividad,jdbcType=NUMERIC}


    and C.COD_SUPERVISOR = #{codSupervisor,jdbcType=NUMERIC}


    and C.FECHA_INICIO_SUPERVISION = #{fechaInicioSupervision,jdbcType=TIMESTAMP}


    and C.DEPENDENCIA_SUPERVISION = #{dependenciaSupervision,jdbcType=VARCHAR}


    and C.LIQUIDADO = #{liquidado,jdbcType=NUMERIC}


    and C.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


    and C.FECHA_LIQUIDACION = #{codPersona,jdbcType=TIMESTAMP}

  OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

select P.COD_PERSONA, P.PRIMER_NOMBRE, P.SEGUNDO_NOMBRE, P.PRIMER_APELLIDO, P.SEGUNDO_APELLIDO, E.NOMBRE_ENTIDAD, C.* from CONTRATO C INNER JOIN ENTIDAD E ON C.COD_ENTIDAD = E.COD_ENTIDAD INNER JOIN PERSONA P ON P.COD_PERSONA = C.COD_PERSONA WHERE 1 = 1
and C.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
and E.CODIGO_SIGEP = #{codigoSigep,jdbcType=VARCHAR}
and P.NUMERO_IDENTIFICACION = #{numeroIdentificacion,jdbcType=VARCHAR}
and P.COD_TIPO_IDENTIFICACION = #{codTipoIdentificacion,jdbcType=VARCHAR}
ORDER BY COD_CONTRATO DESC
