mapper.select:
 	SELECT DC.*, 
		(SELECT NOMBRE_PAIS FROM PAIS WHERE COD_PAIS = DC.COD_PAIS_RESIDENCIA) NOMBRE_PAIS ,
		(SELECT NOMBRE_DEPARTAMENTO FROM DEPARTAMENTO WHERE COD_DEPARTAMENTO = DC.COD_DEPARTAMENTO_RESIDENCIA) NOMBRE_DEPARTAMENTO,
		(SELECT NOMBRE_MUNICIPIO FROM MUNICIPIO WHERE COD_MUNICIPIO = DC.COD_MUNICIPIO_RESIDENCIA) NOMBRE_MUNICIPIO,
		(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = DC.COD_TIPO_ZONA) NOMBRE_PARAMETRO
    from DATO_CONTACTO_BR DC
	  WHERE DC.COD_PERSONA =  #{codPersona,jdbcType=NUMERIC}
	     AND   DC.COD_DECLARACION = #{codDeclaracion,jdbcType=NUMERIC}

           AND DEFINITIVO = #{definitivo,jdbcType=NUMERIC}

   	   OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY
