mapper.select:
	    SELECT de.*,
		(SELECT e.NOMBRE_ENTIDAD FROM ENTIDAD e WHERE e.COD_ENTIDAD = de.COD_ENTIDAD) NOMBRE_ENTIDAD,
		(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = de.COD_CLASE_DEPENDENCIA) NOMBRE_CLASE_DEPENDENCIA
		FROM DEPENDENCIA_ENTIDAD de
		WHERE de.COD_DEPENDENCIA_ENTIDAD > 0

        AND COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


        AND COD_CLASE_DEPENDENCIA = #{codClaseDependencia,jdbcType=NUMERIC}


        AND UPPER(NOMBRE_DEPENDENCIA) LIKE '%'|| UPPER(#{nombreDependencia,jdbcType=VARCHAR})||'%' 


        AND COD_PREDECESOR = #{codPredecesor,jdbcType=NUMERIC}


        AND FUNCIONES_DEPENDENCIA = #{funcionesDependencia,jdbcType=VARCHAR}


        AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


        AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


        AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


        AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


        AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


        AND JERARQUIA = #{jerarquia,jdbcType=VARCHAR}


        AND FLG_ESPECIAL = #{flgEspecial,jdbcType=NUMERIC}


        AND UBICACION_GEO = #{ubicacionGeo,jdbcType=VARCHAR}


        AND CORREO_INSTITUCIONAL = #{correoInstitucional,jdbcType=VARCHAR}


        AND PAGINA_WEB = #{paginaWeb,jdbcType=VARCHAR}


        AND COD_DEPARTAMENTO = #{codDepartamento,jdbcType=NUMERIC}


        AND COD_MUNICIPIO = #{codMunicipio,jdbcType=NUMERIC}


        AND DIRECCION_FISICA = #{direccionFisica,jdbcType=VARCHAR}


        AND INDICADOR = #{indicador,jdbcType=VARCHAR}


        AND TELEFONO = #{telefono,jdbcType=VARCHAR}


        AND FAX = #{fax,jdbcType=VARCHAR}


        AND SIGLA = #{sigla,jdbcType=VARCHAR}


        AND CODIGO_DANE = #{codigoDane,jdbcType=VARCHAR}


        AND COD_POLITICAS_ASOCIADAS = #{codPoliticasAsociadas,jdbcType=NUMERIC}


        AND COD_TIP_DEPENDENCIA_ESPECIAL = #{codTipDependenciaEspecial,jdbcType=NUMERIC}


        AND FUNCIONES_DEPENDENCIA1 = #{funcionesDependencia1,jdbcType=CLOB}



        AND COD_DEPENDENCIA_ENTIDAD = #{codDependenciaEntidad,jdbcType=NUMERIC}


			AND de.COD_PREDECESOR is null


        AND JERARQUIA_PADRE = #{jerarquiaPadre,jdbcType=VARCHAR}



        AND DEPENDENCIA_HIJO = #{flgDependenciaHijo,jdbcType=NUMERIC}


			AND (de.DEPENDENCIA_HIJO is null or de.DEPENDENCIA_HIJO = 0) 



			ORDER BY de.NOMBRE_DEPENDENCIA



			order by de.jerarquia asc


		OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY
