mapper.select:
SELECT

FROM ENTIDAD
WHERE 1=1

	AND COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


	AND CODIGO_SIGEP like
	'%'||#{codigoSigep,jdbcType=VARCHAR}||'%'


	AND COD_DEPARTAMENTO = #{codDepartamento,jdbcType=NUMERIC}


	AND COD_MUNICIPIO = #{codMunicipio,jdbcType=NUMERIC}


	AND COD_CLASIFICACION_ORGANICA =
	#{codClasificacionOrganica,jdbcType=NUMERIC}


	AND COD_ORDEN = #{codOrden,jdbcType=NUMERIC}


	AND COD_SUBORDEN = #{codSuborden,jdbcType=NUMERIC}


	AND COD_NATURALEZA = #{codNaturaleza,jdbcType=NUMERIC}


	AND COD_NIVEL_ADMINISTRATIVO =
	#{codNivelAdministrativo,jdbcType=NUMERIC}


	AND COD_TIPO_ADSCRIPCION =
	#{codTipoAdscripcion,jdbcType=NUMERIC}


	AND COD_ADSCRITA_VINCULADA =
	#{codAdscritaVinculada,jdbcType=NUMERIC}


	AND COD_SISTEMA_CARRERA =
	#{codSistemaCarrera,jdbcType=NUMERIC}


	AND COD_CATEGORIA = #{codCategoria,jdbcType=NUMERIC}


	AND COD_NORMA = #{codNorma,jdbcType=NUMERIC}


	AND COD_RESPONSABLE_GESTION =
	#{codResponsableGestion,jdbcType=NUMERIC}


	AND NIT = #{nit,jdbcType=VARCHAR}


	AND UPPER(translate(NOMBRE_ENTIDAD, '', 'aeiouAEIOU')) like
	'%'||UPPER(translate(#{nombreEntidad,jdbcType=VARCHAR}, '', 'aeiouAEIOU'))||'%'


	AND UPPER(SIGLA) = UPPER(#{sigla,jdbcType=VARCHAR})


	AND COD_ENT_PREDECESOR =
	#{codEntPredecesor,jdbcType=NUMERIC}


	AND FECHA_INICIO = #{fechaInicio,jdbcType=TIMESTAMP}


	AND FECHA_FIN = #{fechaFin,jdbcType=TIMESTAMP}


	AND COD_REPRESENTANTE_LEGAL =
	#{codRepresentanteLegal,jdbcType=VARCHAR}


	AND EMAIL_ENTIDAD = #{emailEntidad,jdbcType=VARCHAR}


	AND LATITUD = #{latitud,jdbcType=NUMERIC}


	AND LONGITUD = #{longitud,jdbcType=NUMERIC}


	AND UPPER(DIRECCION) =
	UPPER(#{direccion,jdbcType=VARCHAR})


	AND TELEFONO_ENTIDAD = #{telefonoEntidad,jdbcType=VARCHAR}


	AND FAX = #{fax,jdbcType=NUMERIC}


	AND COD_ESTADO_ENTIDAD =
	#{codEstadoEntidad,jdbcType=NUMERIC}


	AND FLG_ES_CABEZA = #{flgEsCabeza,jdbcType=NUMERIC}


	AND COALESCE(FLG_DEPENDENCIA_ESPECIAL,0) =
	#{flgDependenciaEspecial,jdbcType=NUMERIC}


	AND ESTATUTO = #{codPestatutoais,jdbcType=NUMERIC}


	AND PERSONERIA_JURIDICA =
	#{personeriaJuridica,jdbcType=NUMERIC}


	AND COD_REGIMEN_SALARIAL =
	#{codRegimenSalarial,jdbcType=NUMERIC}


	AND ESTRUCTURA = #{estructura,jdbcType=NUMERIC}


	AND PLANTA = #{planta,jdbcType=NUMERIC}


	AND MANUAL_FUNCIONES = #{manualFunciones,jdbcType=NUMERIC}


	AND FLG_APLICA_1785 = #{flgAplica1785,jdbcType=NUMERIC}


	AND COD_TIPO_ENTIDAD = #{codTipoEntidad,jdbcType=NUMERIC}


	AND COD_SUBESTADO_ENTIDAD =
	#{codSubestadoEntidad,jdbcType=NUMERIC}


	AND COD_SECTOR_ADMINISTRATIVO =
	#{codSectorAdministrativo,jdbcType=NUMERIC}


	AND COALESCE(ENTIDAD_POSTCONFILCTO,1719) =
	#{entidadPostconfilcto,jdbcType=NUMERIC}


	AND COD_CATEGORIA_MUNICIPIO =
	#{codCategoriaMunicipio,jdbcType=NUMERIC}


	AND CODIGO_CUIN = #{codigoCuin,jdbcType=VARCHAR}


	AND CODIGO_DANE = #{codigoDane,jdbcType=VARCHAR}


	AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	AND PATRIMONIO_PROPIO =
	#{patrimonioPropio,jdbcType=VARCHAR}


	AND TIPO_ZONA = #{tipoZona,jdbcType=NUMERIC}


	AND COD_ESTADO_GESTION =
	#{codEstadoGestion,jdbcType=NUMERIC}


	AND COD_ENTIDAD_ADSCRITA =
	#{codEntidadAdscrita,jdbcType=NUMERIC}

order by NOMBRE_ENTIDAD
OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH
NEXT
#{limitEnd,jdbcType=NUMERIC} ROWS ONLY

select E.*, (SELECT p.VALOR_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = E.COD_SECTOR_ADMINISTRATIVO) NOMBRE_SECTOR_ADMINISTRATIVO, (SELECT p.VALOR_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = E.COD_CLASIFICACION_ORGANICA) NOMBRE_CLASIFICACION_ORGANICA, (SELECT p.VALOR_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = E.COD_NATURALEZA) NOMBRE_NATURALEZA_EMPLEO, (SELECT p.VALOR_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = E.COD_ORDEN) NOMBRE_ORDEN FROM USUARIO_ENTIDAD UE, ENTIDAD E WHERE UE.COD_ENTIDAD = E.COD_ENTIDAD AND UE.COD_USUARIO = #{codUsuarioSesion,jdbcType=NUMERIC} AND UE.FLG_ACTIVO = 1 AND E.COD_ESTADO_ENTIDAD != #{codEstadoEntidad,jdbcType=NUMERIC}
AND E.CODIGO_SIGEP like '%'||#{codigoSigep,jdbcType=VARCHAR}||'%'
AND UPPER(E.NOMBRE_ENTIDAD) like '%'||UPPER(#{nombreEntidad,jdbcType=VARCHAR})||'%'
AND E.COD_SECTOR_ADMINISTRATIVO = #{codSectorAdministrativo,jdbcType=NUMERIC}
AND E.COD_CLASIFICACION_ORGANICA = #{codClasificacionOrganica,jdbcType=NUMERIC}
AND E.COD_NATURALEZA = #{codNaturaleza,jdbcType=NUMERIC}
AND E.COD_ORDEN = #{codOrden,jdbcType=NUMERIC}
