mapper.select:
SELECT ecp.*, (SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = ecp.COD_NATURALEZA_EMPLEO) NOMBRE_NATURALEZA_EMPLEO, (SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = ecp.COD_TIPO_PLANTA) NOMBRE_TIPO_PLANTA, (SELECT distinct de.NOMBRE_DEPENDENCIA FROM DEPENDENCIA_ENTIDAD de WHERE de.COD_DEPENDENCIA_ENTIDAD = ecp.COD_DEPENDENCIA_ENTIDAD) NOMBRE_DEPENDENCIA, ecp.COD_NOMENCLATURA_DENOMINACION, (SELECT distinct d.NOMBRE_DENOMINACION FROM NOMENCLATURA_DENOMINACION nd INNER JOIN DENOMINACION d ON d.COD_DENOMINACION = nd.COD_DENOMINACION WHERE nd.COD_NOMENCLATURA_DENOMINACION = ecp.COD_NOMENCLATURA_DENOMINACION) NOMBRE_CARGO FROM ENTIDAD_PLANTA_DETALLE ecp WHERE ecp.COD_ENTIDAD_PLANTA_DETALLE > 0
AND ecp.COD_ENTIDAD_PLANTA_DETALLE = #{codEntidadPlantaDetalle,jdbcType=NUMERIC}
AND ecp.COD_ENTIDAD_PLANTA = #{codEntidadPlanta,jdbcType=NUMERIC}
AND ecp.COD_TIPO_PLANTA = #{codTipoPlanta,jdbcType=NUMERIC}
AND ecp.COD_TIPO_PLANTA = #{codDependenciaEntidad,jdbcType=NUMERIC}
AND ecp.COD_NATURALEZA_EMPLEO = #{codNaturalezaEmpleo,jdbcType=NUMERIC}
AND ecp.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
AND ecp.COD_NOMENCLATURA_DENOMINACION = #{codNomenclaturaDenominacion,jdbcType=NUMERIC}
OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

select epd.cod_entidad_planta_detalle, CONCAT(d.nombre_denominacion, concat( concat(' - ',p.valor_parametro), concat(' - ',p1.valor_parametro) )) NOMBRE_CARGO from entidad_planta_detalle epd inner join entidad_planta ep on ep.cod_entidad_planta = epd.cod_entidad_planta inner join nomenclatura_denominacion nd on nd.cod_nomenclatura_denominacion = epd.cod_nomenclatura_denominacion inner join denominacion d on d.cod_denominacion = nd.cod_denominacion inner join parametrica p on p.cod_tabla_parametrica = nd.cod_codigo::INTEGER inner join parametrica p1 on p1.cod_tabla_parametrica = nd.cod_grado where 1=1
and ep.cod_clasificacion_planta = #{codClasificacionPlanta,jdbcType=NUMERIC}
and ep.cod_clase_planta = #{codClasePlanta,jdbcType=NUMERIC}
AND ep.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
and ep.FLG_ACTIVO = 1 and epd.FLG_ACTIVO = 1 order by NOMBRE_CARGO asc

select epd.cod_entidad_planta_detalle, d.nombre_denominacion NOMBRE_CARGO,p1.valor_parametro nombre_grado, p.valor_parametro NOMBRE_CODIGO from entidad_planta_detalle epd inner join entidad_planta ep on ep.cod_entidad_planta = epd.cod_entidad_planta inner join nomenclatura_denominacion nd on nd.cod_nomenclatura_denominacion = epd.cod_nomenclatura_denominacion inner join denominacion d on d.cod_denominacion = nd.cod_denominacion inner join parametrica p on p.cod_tabla_parametrica = nd.cod_codigo inner join parametrica p1 on p1.cod_tabla_parametrica = nd.cod_grado where 1=1
AND ep.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
and ep.FLG_ACTIVO = 1 and epd.FLG_ACTIVO = 1 order by NOMBRE_CARGO asc

SELECT p.valor_parametro NOMBRE_CODIGO, d.nombre_denominacion NOMBRE_DENOMINACION from entidad_planta_detalle epd inner join entidad_planta ep on ep.cod_entidad_planta = epd.cod_entidad_planta inner join nomenclatura_denominacion nd on nd.cod_nomenclatura_denominacion = epd.cod_nomenclatura_denominacion inner join denominacion d on d.cod_denominacion = nd.cod_denominacion inner join parametrica p on p.cod_tabla_parametrica = nd.cod_codigo inner join parametrica pl on pl.cod_tabla_parametrica = nd.cod_grado where 1=1
AND ep.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
and ep.FLG_ACTIVO = 1 and epd.FLG_ACTIVO = 1

SELECT count(*) over() TOTAL, EPD.COD_ENTIDAD_PLANTA_DETALLE, EPD.COD_ENTIDAD_PLANTA, EPD.COD_TIPO_PLANTA, EPD.COD_DEPENDENCIA_ENTIDAD, EPD.COD_NATURALEZA_EMPLEO, EPD.FLG_REPRESENTANTE_LEGAL, EPD.FLG_ES_GERENCIA_PUBLICA, EPD.CANTIDAD_CARGO_APROPIACION_PRESUPUESTAL, EPD.FLG_CARGO_EXPUESTO_PUBLICAMENTE, EP.COD_CLASIFICACION_PLANTA, EP.COD_ENTIDAD, EPD.FLG_ACTIVO, EP.COD_NORMA, EP.COD_CLASE_PLANTA, D.NOMBRE_DENOMINACION AS NOMBRE_CARGO, D.NOMBRE_DENOMINACION, ND.COD_GRADO, ND.COD_CODIGO, ND.COD_NIVEL_JERARQUICO AS COD_NIVEL_CARGO, EPD.COD_ENTIDAD_PLANTA_DETALLE AS COD_CARGO, D.COD_DENOMINACION, ND.ASIGNACION_SALARIAL, EPD.CANTIDAD_CARGO, ep.nombre_planta as NOMBRE_PLANTA, nombre_parametro(ND.COD_NIVEL_JERARQUICO) AS NOMBRE_NIVEL, nombre_parametro(ND.COD_GRADO) as NOMBRE_GRADO, nombre_parametro(ND.COD_CODIGO) as NOMBRE_CODIGO, nombre_parametro(EPD.COD_TIPO_PLANTA) as NOMBRE_TIPO_PLANTA, de.nombre_dependencia, nombre_parametro(EPD.COD_NATURALEZA_EMPLEO) as NOMBRE_NATURALEZA_EMPLEO FROM ENTIDAD_PLANTA_DETALLE EPD INNER JOIN ENTIDAD_PLANTA EP ON EP.COD_ENTIDAD_PLANTA = EPD.COD_ENTIDAD_PLANTA INNER JOIN NOMENCLATURA_DENOMINACION ND ON ND.COD_NOMENCLATURA_DENOMINACION = EPD.COD_NOMENCLATURA_DENOMINACION INNER JOIN DENOMINACION D ON D.COD_DENOMINACION = ND.COD_DENOMINACION LEFT JOIN dependencia_entidad de on de.cod_dependencia_entidad = EPD.COD_DEPENDENCIA_ENTIDAD WHERE 1 = 1
AND EP.COD_ENTIDAD = #{codEntidad, jdbcType=NUMERIC}
AND EP.COD_ENTIDAD_PLANTA = #{codEntidadPlanta, jdbcType=NUMERIC}
AND EP.COD_NORMA = #{codNorma,jdbcType=NUMERIC}
AND EPD.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
AND EP.COD_TIPO_PLANTA = #{codTipoPlanta,jdbcType=NUMERIC}
AND EPD.COD_ENTIDAD_PLANTA_DETALLE = #{codCargo,jdbcType=NUMERIC}
AND EPD.COD_ENTIDAD_PLANTA_DETALLE = #{codEntidadPlantaDetalle,jdbcType=NUMERIC}
AND EPD.COD_NATURALEZA_EMPLEO = #{codNaturalezaEmpleo,jdbcType=NUMERIC}
AND ND.COD_NOMENCLATURA_DENOMINACION = #{codNomenclaturaDenominacion,jdbcType=NUMERIC}
OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

mapper.update:
UPDATE entidad_planta_detalle epd
SET
pdd.cod_nomenclatura_denominacion =
#{codDenominacionDestino,jdbcType=NUMERIC}
where epd.cod_nomenclatura_denominacion =
#{codNomenclaturaDenominacion,jdbcType=NUMERIC}
and epd.flg_activo = #{flgActivo,jdbcType=NUMERIC}

mapper.select:
SELECT
SUM(CARGO) AS TOTAL_CARGOS_DIRECTIVOS FROM(
SELECT
EPD.CANTIDAD_CARGO_APROPIACION_PRESUPUESTAL,
D.NOMBRE_DENOMINACION AS NOMBRE_CARGO,
EPD.COD_ENTIDAD_PLANTA_DETALLE AS COD_CARGO,
EPD.CANTIDAD_CARGO AS CARGO
FROM ENTIDAD_PLANTA_DETALLE EPD
INNER JOIN ENTIDAD_PLANTA EP ON EP.COD_ENTIDAD_PLANTA =
EPD.COD_ENTIDAD_PLANTA
INNER JOIN NOMENCLATURA_DENOMINACION ND ON ND.COD_NOMENCLATURA_DENOMINACION
= EPD.COD_NOMENCLATURA_DENOMINACION
INNER JOIN DENOMINACION D ON D.COD_DENOMINACION = ND.COD_DENOMINACION
WHERE
1 = 1

	AND EP.COD_ENTIDAD = #{codEntidad, jdbcType=NUMERIC}


	AND ND.cod_nivel_jerarquico = #{codNivelCargo,
	jdbcType=NUMERIC}


	AND EP.COD_ENTIDAD_PLANTA = #{codEntidadPlanta,
	jdbcType=NUMERIC}


	AND EP.COD_NORMA = #{codNorma,jdbcType=NUMERIC}


	AND EP.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	AND EPD.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	AND EP.flg_guardado_parcial =
	#{flgGuardadoParcial,jdbcType=NUMERIC}


	AND EP.COD_TIPO_PLANTA = #{codTipoPlanta,jdbcType=NUMERIC}


	AND EPD.COD_ENTIDAD_PLANTA_DETALLE =
	#{codCargo,jdbcType=NUMERIC}


	AND EPD.COD_ENTIDAD_PLANTA_DETALLE =
	#{codEntidadPlantaDetalle,jdbcType=NUMERIC}


	AND EPD.COD_NATURALEZA_EMPLEO =
	#{codNaturalezaEmpleo,jdbcType=NUMERIC}

GROUP BY EPD.CANTIDAD_CARGO_APROPIACION_PRESUPUESTAL,
D.NOMBRE_DENOMINACION,
EPD.COD_ENTIDAD_PLANTA_DETALLE,
EPD.CANTIDAD_CARGO )

SELECT epp.nombre_planta, CONCAT (CONCAT(CONCAT(CONCAT(p.primer_nombre, ' '),p.segundo_nombre),' '), p.primer_apellido) NOMBRE_PERSONA FROM VINCULACION v INNER JOIN ENTIDAD_PLANTA_DETALLE epd ON epd.COD_ENTIDAD_PLANTA_DETALLE = v.COD_ENTIDAD_PLANTA_DETALLE INNER JOIN NOMENCLATURA_DENOMINACION nd on epd.COD_NOMENCLATURA_DENOMINACION = nd.COD_NOMENCLATURA_DENOMINACION INNER JOIN DENOMINACION d ON nd.COD_DENOMINACION = d.COD_DENOMINACION INNER JOIN ENTIDAD_PLANTA epp ON epp.COD_ENTIDAD_PLANTA = epd.COD_ENTIDAD_PLANTA INNER JOIN PERSONA P on p.cod_persona = v.cod_persona WHERE 1=1
AND ND.COD_NOMENCLATURA_DENOMINACION = #{codNomenclaturaDenominacion,jdbcType=NUMERIC}
AND v.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
AND epp.COD_ENTIDAD = #{codEntidad, jdbcType=NUMERIC}
