mapper.select:
SELECT distinct epp.COD_CLASIFICACION_PLANTA, (SELECT p.NOMBRE_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = epp.COD_CLASIFICACION_PLANTA) NOMBRE_CLASIFICACION_PLANTA FROM ENTIDAD_PLANTA epp WHERE epp.COD_ENTIDAD_PLANTA > 0 AND COALESCE(epp.fecha_fin, NOW() + INTERVAL '1 day') > NOW()
AND epp.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
AND epp.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
ORDER BY NOMBRE_CLASIFICACION_PLANTA OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

SELECT epp.COD_CLASE_PLANTA, (SELECT p.NOMBRE_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = epp.COD_CLASE_PLANTA) NOMBRE_CLASE_PLANTA FROM ENTIDAD_PLANTA epp WHERE epp.COD_ENTIDAD_PLANTA > 0 AND COALESCE(epp.fecha_fin, NOW() + INTERVAL '1 day') > NOW()
AND epp.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
AND epp.COD_CLASIFICACION_PLANTA = #{codClasificacionPlanta,jdbcType=NUMERIC}
AND epp.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
ORDER BY NOMBRE_CLASE_PLANTA OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

	    SELECT distinct epp.*, n.OBJETO_NORMA, n.COD_TIPO_NORMA,
		    (SELECT p.VALOR_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = epp.COD_CLASIFICACION_PLANTA) NOMBRE_CLASIFICACION_PLANTA,
            (SELECT p.VALOR_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = epp.COD_CLASE_PLANTA) NOMBRE_CLASE_PLANTA,
            (SELECT p.VALOR_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = n.COD_TIPO_NORMA) NOMBRE_TIPO_NORMA
		FROM 
		    ENTIDAD_PLANTA epp
		   	LEFT OUTER JOIN NORMA n ON n.COD_NORMA =  epp.COD_NORMA
		   	LEFT OUTER JOIN ENTIDAD_PLANTA_DETALLE ECP ON epp.COD_ENTIDAD_PLANTA = ecp.COD_ENTIDAD_PLANTA
		WHERE 1=1

			AND epp.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


			AND epp.COD_CLASIFICACION_PLANTA = #{codClasificacionPlanta,jdbcType=NUMERIC}


			AND epp.COD_CLASE_PLANTA = #{codClasePlanta,jdbcType=NUMERIC}


			AND epp.COD_NORMA = #{codNorma,jdbcType=NUMERIC}


			AND epp.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


			AND epp.JUSTIFICACION = #{justificacion,jdbcType=VARCHAR}


			AND epp.FLG_GUARDADO_PARCIAL = #{flgGuardadoParcial,jdbcType=NUMERIC}


			AND n.COD_TIPO_NORMA = #{codTipoNorma,jdbcType=NUMERIC}


			AND n.FECHA_NORMA = #{fechaNorma,jdbcType=TIMESTAMP}


			AND n.NUMERO_NORMA = #{numeroNorma,jdbcType=TIMESTAMP}


           AND epp.COD_NOMENCLATURA = #{codNomenclatura,jdbcType=NUMERIC}


			AND ECP.COD_TIPO_PLANTA = #{codTipoPlanta,jdbcType=NUMERIC}


			AND EXTRACT(YEAR FROM n.FECHA_NORMA) = #{annoNorma,jdbcType=NUMERIC}


            AND NOMBRE_PLANTA = #{nombrePlanta,jdbcType=VARCHAR}


	    OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

	    SELECT 

		FROM 
		    ENTIDAD_PLANTA
		WHERE 1=1

	        AND COD_NORMA = #{codNorma,jdbcType=NUMERIC}


	        AND COD_CLASIFICACION_PLANTA = #{codClasificacionPlanta,jdbcType=NUMERIC}


	        AND COD_CLASE_PLANTA = #{codClasePlanta,jdbcType=NUMERIC}


	        AND FECHA_INICIO = #{fechaInicio,jdbcType=TIMESTAMP}


	        AND FECHA_FIN = #{fechaFin,jdbcType=TIMESTAMP}


	        AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


	        AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


	        AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


	        AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


	        AND COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


	        AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	        AND FLG_GUARDADO_PARCIAL = #{flgGuardadoParcial,jdbcType=NUMERIC}


	        AND JUSTIFICACION = #{justificacion,jdbcType=VARCHAR}


	        AND COD_NOMENCLATURA = #{codNomenclatura,jdbcType=NUMERIC}


            AND NOMBRE_PLANTA = #{nombrePlanta,jdbcType=VARCHAR}

		ORDER BY AUD_FECHA_ACTUALIZACION DESC
	    OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY

	  SELECT distinct epp.*, n.OBJETO_NORMA, n.COD_TIPO_NORMA,
		    (SELECT p.NOMBRE_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = epp.COD_CLASIFICACION_PLANTA) NOMBRE_CLASIFICACION_PLANTA,
            (SELECT p.NOMBRE_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = epp.COD_CLASE_PLANTA) NOMBRE_CLASE_PLANTA,
            (SELECT p.NOMBRE_PARAMETRO FROM PARAMETRICA p WHERE p.COD_TABLA_PARAMETRICA = n.COD_TIPO_NORMA) NOMBRE_TIPO_NORMA
		FROM 
		    ENTIDAD_PLANTA epp
		   	LEFT OUTER JOIN NORMA n ON n.COD_NORMA =  epp.COD_NORMA
		   	LEFT OUTER JOIN ENTIDAD_PLANTA_DETALLE ECP ON epp.COD_ENTIDAD_PLANTA = ecp.COD_ENTIDAD_PLANTA
		WHERE 1=1
		 and COALESCE(epp.fecha_fin, CURRENT_DATE) >= CURRENT_DATE

			AND epp.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


			AND epp.COD_CLASIFICACION_PLANTA != #{codClasificacionPlanta,jdbcType=NUMERIC}


			AND epp.COD_CLASE_PLANTA = #{codClasePlanta,jdbcType=NUMERIC}


			AND epp.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


			AND epp.FLG_GUARDADO_PARCIAL = #{flgGuardadoParcial,jdbcType=NUMERIC}
