mapper.select:
select

from ESCALA_SALARIAL
	where 1 = 1

    AND COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


    AND COD_NIVEL_CARGO = #{codNivelCargo,jdbcType=NUMERIC}


    AND COD_NORMA = #{codNorma,jdbcType=NUMERIC}


    AND COD_GRADO = #{codGrado,jdbcType=NUMERIC}


    AND ASIGNACION_BASICA = #{asignacionBasica,jdbcType=NUMERIC}


    AND GASTOS_REPRESENTACION = #{gastosRepresentacion,jdbcType=NUMERIC}


    AND COD_FACTOR_SALARIAL = #{codFactorSalarial,jdbcType=NUMERIC}


    AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


    AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


    AND NIVEL_JERARQUICO = #{nivelJerarquico,jdbcType=NUMERIC}


    AND OTRAS_ASIGNACIONES = #{otrasAsignaciones,jdbcType=NUMERIC}


    AND VALOR_ASIGNACION = #{valorAsignacion,jdbcType=NUMERIC}


    AND PORCENTAJE_ASIGNACION = #{porcentajeAsignacion,jdbcType=NUMERIC}
