mapper.select:
select ED.*, (SELECT NOMBRE_ENTIDAD FROM ENTIDAD WHERE COD_ENTIDAD = ED.COD_ENTIDAD) NOMBRE_ENTIDAD, (SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = ED.COD_TIPO_EVALUACION) NOMBRE_TIPO_EVALUACION, (SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = ED.COD_NIVEL_CUMPLIMIENTO) NOMBRE_NIVEL_CUMPLIMIENTO from EVALUACION_DESEMPENO ED WHERE ED.COD_PERSONA = #{codPersona,jdbcType=NUMERIC}
and ED.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
ORDER BY ED.FECHA_INICIO_EVALUACION DESC OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY

   select EV.* , P.NOMBRE_PARAMETRO AS NOMBRE_TIPO_EVALUACION, EP.FLG_ENTIDAD_PUBLICA,
		P2.NOMBRE_PARAMETRO AS NOMBRE_NIVEL_CUMPLIMIENTO, CA.NOMBRE_DENOMINACION AS NOMBRE_CARGO
        from EVALUACION_DESEMPENO EV
            LEFT JOIN  PARAMETRICA P on EV.COD_TIPO_EVALUACION = P.COD_TABLA_PARAMETRICA
            LEFT JOIN  PARAMETRICA P2 on EV.COD_NIVEL_CUMPLIMIENTO = P2.COD_TABLA_PARAMETRICA
            LEFT JOIN  EXPERIENCIA_PROFESIONAL EP ON EV.COD_EXPERIENCIA_PROFESIONAL = EP.COD_EXPERIENCIA_PROFESIONAL
            LEFT JOIN  DENOMINACION CA ON EP.COD_CARGO = CA.COD_DENOMINACION
		 WHERE 1 = 1

	      AND  EV.COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


	      AND    COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


	       AND   CARGO_GRADO = #{cargoGrado,jdbcType=VARCHAR}


	       AND   COD_TIPO_EVALUACION = #{codTipoEvaluacion,jdbcType=NUMERIC}


	       AND   FECHA_INICIO_EVALUACION = #{fechaInicioEvaluacion,jdbcType=TIMESTAMP}


	       AND   FECHA_FIN_EVALUACION = #{fechaFinEvaluacion,jdbcType=TIMESTAMP}


	       AND   ESCALA_EVALUACION = #{escalaEvaluacion,jdbcType=VARCHAR}


	       AND   RESULTADO_EVALUACION = #{resultadoEvaluacion,jdbcType=NUMERIC}


	       AND   VALOR_MINIMO_ESCALA = #{valorMinimoEscala,jdbcType=VARCHAR}


	       AND   VALOR_MAXIMO_ESCALA = #{valorMaximoEscala,jdbcType=VARCHAR}


	       AND   COD_NIVEL_CUMPLIMIENTO = #{codNivelCumplimiento,jdbcType=NUMERIC}


	       AND   FLG_EVALUACION_ENTIDAD_PUBLICA = #{flgEvaluacionEntidadPublica,jdbcType=NUMERIC}


	       AND   NOMBRE_ENTIDAD_PUBLICA = #{nombreEntidadPublica,jdbcType=VARCHAR}


	       AND   CARGO_ENTIDAD_PRIVADA = #{cargoEntidadPrivada,jdbcType=VARCHAR}


	       AND   AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


	       AND   AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


	       AND   AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


	       AND   AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


	       AND   EV.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
