mapper.select:
		 SELECT 

   	  FROM LOGRO_RECURSO
   	  WHERE 1 = 1

       AND COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


       AND FLG_ADMINISTRA_RECURSOS = #{flgAdministraRecursos,jdbcType=NUMERIC}


       AND NOMBRE_ENTIDAD = #{nombreEntidad,jdbcType=VARCHAR}


       AND NUM_EMPLEADOS = #{numEmpleados,jdbcType=NUMERIC}


       AND FLG_PERSONAS_CARGO = #{flgPersonasCargo,jdbcType=NUMERIC}


       AND NUM_PERSONAS_CARGO = #{numPersonasCargo,jdbcType=NUMERIC}


       AND FLG_RECURSO_ECONOMICO = #{flgRecursoEconomico,jdbcType=NUMERIC}


       AND VALOR_RECURSO_ECONOMICO = #{valorRecursoEconomico,jdbcType=NUMERIC}


       AND LOGRO_SOBRESALIENTE = #{logroSobresaliente,jdbcType=VARCHAR}


       AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


       AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


       AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


       AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


       AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


       AND COD_EXPERIENCIA_PROFESIONAL = #{codExperienciaProfesional,jdbcType=NUMERIC}


       AND COD_TIPO_REGISTRO = #{codTipoRegistro,jdbcType=VARCHAR}

		 SELECT 

   	  FROM LOGRO_RECURSO LR
   	  LEFT JOIN EXPERIENCIA_PROFESIONAL ep ON ep.COD_EXPERIENCIA_PROFESIONAL = LR.COD_EXPERIENCIA_PROFESIONAL
   	  WHERE (EP.COD_NIVEL_JERARQUICO_HV in(498,499,509,524,525) or ep.cod_tipo_entidad = 143)

       AND LR.COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


       AND LR.FLG_ADMINISTRA_RECURSOS = #{flgAdministraRecursos,jdbcType=NUMERIC}


       AND LR.NOMBRE_ENTIDAD = #{nombreEntidad,jdbcType=VARCHAR}


       AND LR.NUM_EMPLEADOS = #{numEmpleados,jdbcType=NUMERIC}


       AND LR.FLG_PERSONAS_CARGO = #{flgPersonasCargo,jdbcType=NUMERIC}


       AND LR.NUM_PERSONAS_CARGO = #{numPersonasCargo,jdbcType=NUMERIC}


       AND LR.FLG_RECURSO_ECONOMICO = #{flgRecursoEconomico,jdbcType=NUMERIC}


       AND LR.VALOR_RECURSO_ECONOMICO = #{valorRecursoEconomico,jdbcType=NUMERIC}


       AND LR.LOGRO_SOBRESALIENTE = #{logroSobresaliente,jdbcType=VARCHAR}


       AND LR.AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


       AND LR.AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


       AND LR.AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


       AND LR.AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


       AND LR.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


       AND LR.COD_EXPERIENCIA_PROFESIONAL = #{codExperienciaProfesional,jdbcType=NUMERIC}


       AND LR.COD_TIPO_REGISTRO = #{codTipoRegistro,jdbcType=VARCHAR}
