mapper.select:
select nd.*,
p.nombre_parametro CODIGO_CARGO,
pa.nombre_parametro GRADO_CARGO,
pe.nombre_parametro NIVEL_CARGO,
d.nombre_denominacion NOMBRE_CARGO
from NOMENCLATURA_DENOMINACION nd
left join parametrica p on p.cod_tabla_parametrica = nd.cod_codigo
left join parametrica pa on pa.cod_tabla_parametrica = nd.cod_grado
left join parametrica pe on pe.cod_tabla_parametrica = nd.cod_nivel_jerarquico
left join denominacion d on d.cod_denominacion = nd.cod_denominacion
where 1 = 1

   AND nd.COD_NOMENCLATURA_DENOMINACION = #{codNomenclaturaDenominacion,jdbcType=NUMERIC}


   AND nd.COD_NOMENCLATURA = #{codNomenclatura,jdbcType=NUMERIC}


    AND nd.COD_NIVEL_JERARQUICO = #{codNivelJerarquico,jdbcType=NUMERIC}


    AND nd.COD_DENOMINACION = #{codDenominacion,jdbcType=NUMERIC}


    AND nd.COD_GRADO = #{codGrado,jdbcType=NUMERIC}


    AND nd.ASIGNACION_SALARIAL = #{asignacionSalarial,jdbcType=NUMERIC}


    AND nd.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


    AND nd.FLG_REQUERIDO = #{flgRequerido,jdbcType=NUMERIC}


   AND  nd.AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


   AND  nd.AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


   AND  nd.AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


    AND  nd.INC_PORCENTAJE = #{incPorcentaje,jdbcType=NUMERIC}


    AND  nd.INC_FECHA = #{incFecha,jdbcType=TIMESTAMP}


    AND  nd.COD_CODIGO = #{codCodigo,jdbcType=VARCHAR}


    AND nd.COD_DENOMINACION_ASOCIACION = #{codDenominacionAsociacion,jdbcType=NUMERIC}


    AND nd.FLG_ACTIVO_PARTICULAR = #{flgActivoParticular,jdbcType=NUMERIC}

select DISTINCT nd.cod_nomenclatura_denominacion, CONCAT(d.nombre_denominacion, concat( concat(' - ',p.nombre_parametro), concat(' - ',p1.nombre_parametro) )) NOMBRE_CARGO, count(*) over() TOTAL from nomenclatura_denominacion nd inner join nomenclatura n on nd.cod_nomenclatura = n.cod_nomenclatura inner join denominacion d on d.cod_denominacion = nd.cod_denominacion inner join parametrica p on p.cod_tabla_parametrica = nd.cod_codigo inner join parametrica p1 on p1.cod_tabla_parametrica = nd.cod_grado where 1=1
and n.cod_entidad = #{codEntidad, jdbcType=NUMERIC}
and nd.flg_activo=1 and n.flg_activo = 1 order by NOMBRE_CARGO OFFSET #{limitIni,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitFin,jdbcType=NUMERIC} ROWS ONLY

 	select nd.cod_grado, p.valor_parametro GRADO_CARGO
	from   nomenclatura_denominacion nd, parametrica p
	where  nd.cod_nomenclatura = #{codNomenclatura,jdbcType=NUMERIC}
		and    nd.flg_activo = #{flgActivo,jdbcType=NUMERIC}
		and    nd.cod_nivel_jerarquico = #{codNivelJerarquico,jdbcType=NUMERIC}
		and    nd.cod_denominacion =  #{codDenominacion,jdbcType=NUMERIC}
		and    nd.cod_codigo =  #{codCodigo,jdbcType=VARCHAR}
		and    nd.cod_grado = p.COD_TABLA_PARAMETRICA
		order by p.nombre_parametro asc

 	select distinct nd.cod_codigo, p.valor_parametro CODIGO_CARGO
	from   nomenclatura_denominacion nd, parametrica p
	where  nd.cod_nomenclatura = #{codNomenclatura,jdbcType=NUMERIC}
		and    nd.flg_activo = #{flgActivo,jdbcType=NUMERIC}
		and    nd.cod_nivel_jerarquico = #{codNivelJerarquico,jdbcType=NUMERIC}
		and    nd.cod_denominacion =  #{codDenominacion,jdbcType=NUMERIC}
		and    nd.cod_codigo = p.COD_TABLA_PARAMETRICA
