mapper.select:
	select n.*, p1.nombre_parametro NOMBRE_RAMA_ORGANIZACIONAL, p2.nombre_parametro NOMBRE_SISTEMA_CARRERA
  from NOMENCLATURA n
  left join parametrica p1 on p1.cod_tabla_parametrica = n.COD_RAMA_ORGANIZACIONAL
  left join parametrica p2 on p2.cod_tabla_parametrica = n.COD_SISTEMA_CARRERA
  WHERE 1 = 1

      AND n.COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}


      AND UPPER(n.NOMBRE_NOMENCLATURA) LIKE '%'|| UPPER(#{nombreNomenclatura,jdbcType=VARCHAR}) ||'%'


      AND n.COD_RAMA_ORGANIZACIONAL = #{codRamaOrganizacional,jdbcType=NUMERIC}


      AND n.COD_SISTEMA_CARRERA = #{codSistemaCarrera,jdbcType=NUMERIC}


      AND n.COD_NORMA = #{codNorma,jdbcType=NUMERIC}


      AND n.NUM_NORMA = #{numNorma,jdbcType=VARCHAR}


      AND n.FECHA_NORMA = #{fechaNorma,jdbcType=TIMESTAMP}


      AND n.JUSTIFICACION = #{justificacion,jdbcType=VARCHAR}


      AND n.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


      AND n.FLG_GENERICA = #{flgGenerica,jdbcType=NUMERIC}


      AND n.FLG_DEFINITIVO = #{flgDefinitivo,jdbcType=NUMERIC}


      AND n.COD_NOMENCLATURA_ASOCIADA = #{codNomenclaturaAsociada,jdbcType=NUMERIC}


      AND n.AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


     AND n.AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


      AND n.AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


      AND n.AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


      AND n.CODIGO_NOMENCLATURA = #{codigoNomenclatura,jdbcType=VARCHAR}


      AND n.cod_nomenclatura = #{codNomenclatura,jdbcType=VARCHAR}

    ORDER BY n.nombre_nomenclatura asc

select e.nombre_entidad as NOMBRE_ENTIDAD, ep.nombre_planta as NOMBRE_PLANTA
from nomenclatura n
	left join entidad e on e.cod_entidad = n.cod_entidad
	inner join entidad_planta ep on ep.cod_nomenclatura = n.cod_nomenclatura
where n.cod_nomenclatura_asociada = #{codNomenclaturaAsociada,jdbcType=NUMERIC}
	and ep.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
