mapper.select:
select O.*, (select NOMBRE_PARAMETRO from PARAMETRICA WHERE COD_TABLA_PARAMETRICA = O.COD_MODALIDAD ) AS MODALIDAD_NOMBRE, (select NOMBRE_PARAMETRO from PARAMETRICA WHERE COD_TABLA_PARAMETRICA = O.COD_MEDIO_CAPACITACION ) AS MEDIO_CAPACITACION_NOMBRE, (select NOMBRE_PAIS from PAIS WHERE COD_PAIS= O.COD_PAIS ) AS PAIS_NOMBRE from OTRO_CONOCIMIENTO O where COD_PERSONA = #{codPersona,jdbcType=NUMERIC}
AND O.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
AND O.FLG_ACTIVO = 1
order by fecha_finalizacion desc
