mapper.select:
    select
      A.*,
     (SELECT NOMBRE_PAIS FROM PAIS WHERE COD_PAIS = A.COD_PAIS) NOMBRE_PAIS ,
	 (SELECT NOMBRE_DEPARTAMENTO FROM DEPARTAMENTO WHERE COD_DEPARTAMENTO = A.COD_DEPARTAMENTO) NOMBRE_DEPARTAMENTO,
	 (SELECT NOMBRE_MUNICIPIO FROM MUNICIPIO WHERE COD_MUNICIPIO = A.COD_MUNICIPIO) NOMBRE_MUNICIPIO
      from PARTICIPACION_PROYECTO A
      WHERE 1 = 1

        ANDCOD_PARTICIPACION_PROYECTO = #{codParticipacionProyecto,jdbcType=NUMERIC},


        AND COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


        AND NOMBRE_ENTIDAD = #{nombreEntidad,jdbcType=VARCHAR}


        AND NOMBRE_PROYECTO = #{nombreProyecto,jdbcType=VARCHAR}


        AND ROL_LABORADO = #{rolLaborado,jdbcType=VARCHAR}


        AND COD_PAIS = #{codPais,jdbcType=NUMERIC}


        AND COD_DEPARTAMENTO = #{codDepartamento,jdbcType=NUMERIC}


        AND COD_MUNICIPIO = #{codMunicipio,jdbcType=NUMERIC}


        AND FECHA_INICIO = #{fechaInicio,jdbcType=TIMESTAMP}


        AND FECHA_TERMINACION = #{fechaTerminacion,jdbcType=TIMESTAMP}


        AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


        AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


        AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


        AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


        AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
