mapper.select:

select
(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = COD_GENERO) NOMBRE_GENERO,
(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = COD_TIPO_IDENTIFICACION) NOMBRE_TIPO_DOCUMENTO,
(SELECT NOMBRE_PARAMETRO FROM PARAMETRICA WHERE COD_TABLA_PARAMETRICA = COD_TIPO_PARENTESCO) NOMBRE_PARENTESCO,
PERSONA_PARENTESCO.*
from PERSONA_PARENTESCO
WHERE PERSONA_PARENTESCO.COD_PERSONA > 0

     AND  COD_DECLARACION = #{codDeclaracion,jdbcType=NUMERIC}


 	AND 	 COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


 	AND 	FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


 	AND 	FLG_SOCIEDAD_CONYUGAL_ACTIVA = #{flgSociedadConyugalActiva,jdbcType=NUMERIC}


 	AND 	COD_TIPO_PARENTESCO = #{codTipoParentesco,jdbcType=NUMERIC}


 	AND 	COD_TIPO_IDENTIFICACION= #{codTipoIdentificacion,jdbcType=NUMERIC}


 	AND 	NUM_IDENTIFICACION =	#{numIdentificacion,jdbcType=VARCHAR}
