mapper.select:

  select po.COD_PREGUNTA_OPINION,po.PREGUNTA,po.FECHA_INICIO,po.FECHA_FIN,po.AUD_FECHA_ACTUALIZACION,
		   po.AUD_COD_USUARIO,po.AUD_COD_ROL,po.AUD_ACCION, FLG_ACTIVO,  count(*) over() TOTAL,
     (CASE WHEN (SELECT COUNT(*) COD_PREGUNTA_OPINION  FROM RESULTADO_PREGUNTA rs WHERE rs.COD_PREGUNTA_OPINION = po.COD_PREGUNTA_OPINION) > 0 THEN 1 ELSE 0 END) TOTAL_RESPUESTA
     from PREGUNTA_OPINION po
    WHERE 1=1

     AND upper(utl_raw.cast_to_varchar2((nlssort(po.PREGUNTA, 'nls_sort=binary_ai')))) like upper(utl_raw.cast_to_varchar2((nlssort('%'||#{pregunta,jdbcType=VARCHAR}||'%', 'nls_sort=binary_ai'))))


      AND po.fecha_Inicio >= #{fechaInicio,jdbcType=VARCHAR}
      AND po.fecha_fin <= #{fechaFin,jdbcType=VARCHAR}


       AND po.fecha_Inicio = #{fechaInicio,jdbcType=VARCHAR}


       AND po.fecha_fin = #{fechaFin,jdbcType=VARCHAR}

     order by po.fecha_inicio DESC
     OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

select po.*,  count(*) over() TOTAL,
  (CASE WHEN (SELECT COUNT(*) COD_PREGUNTA_OPINION  FROM RESULTADO_PREGUNTA rs WHERE rs.COD_PREGUNTA_OPINION = po.COD_PREGUNTA_OPINION) > 0 THEN 1 ELSE 0 END) totalRespuesta
  from PREGUNTA_OPINION po
  where po.fecha_Inicio >= #{fechaInicio,jdbcType=TIMESTAMP} 
   AND FLG_ACTIVO = 1 AND po.fecha_fin={fechaFin,jdbcType=TIMESTAMP} 
  OFFSET #{limitInit,jdbcType=NUMERIC} ROWS FETCH NEXT #{limitEnd,jdbcType=NUMERIC} ROWS ONLY

mapper.insert:

insert into PREGUNTA_OPINION (PREGUNTA, FECHA_INICIO, 
  FECHA_FIN, AUD_FECHA_ACTUALIZACION, AUD_COD_USUARIO, 
  AUD_COD_ROL, AUD_ACCION, FLG_ACTIVO
  )
values (#{pregunta,jdbcType=VARCHAR}, #{fechaInicio,jdbcType=TIMESTAMP}, 
  #{fechaFin,jdbcType=TIMESTAMP}, #{audFechaActualizacion,jdbcType=TIMESTAMP}, #{audCodUsuario,jdbcType=NUMERIC}, 
  #{audCodRol,jdbcType=NUMERIC}, #{audAccion,jdbcType=NUMERIC}, #{flgActivo,jdbcType=NUMERIC}
  )

mapper.select:
select

from PREGUNTA_OPINION po
where po.pregunta = #{pregunta,jdbcType=VARCHAR}
