mapper.insert:

insert into PROGRAMA_ACADEMICO (COD_INSTITUCION, 
  COD_PROGRAMA_ACADEMICO, NOMBRE_PROGRAMA_ACADEMICO, 
  NOMBRE_TITULO_OTORGADO, FLG_ACTIVO, AUD_FECHA_ACTUALIZACION, 
  AUD_COD_USUARIO, AUD_COD_ROL, AUD_ACCION
  )
values (#{codInstitucion,jdbcType=NUMERIC}, 
  #{codProgramaAcademico,jdbcType=NUMERIC}, #{nombreProgramaAcademico,jdbcType=VARCHAR}, 
  #{nombreTituloOtorgado,jdbcType=VARCHAR}, #{flgActivo,jdbcType=NUMERIC}, #{audFechaActualizacion,jdbcType=TIMESTAMP}, 
  #{audCodUsuario,jdbcType=NUMERIC}, #{audCodRol,jdbcType=NUMERIC}, #{audAccion,jdbcType=NUMERIC}
  )

mapper.select:
	    select pa.*,
	    case when pa.cod_programa_academico is null then 'EXTRANJERA'::VARCHAR else COD_PROGRAMA_ACADEMICO::VARCHAR END programa_academico,
	    ie.nombre_institucion
	    from PROGRAMA_ACADEMICO pa
	    inner join institucion_Educativa ie on ie.cod_institucion_educativa = pa.cod_institucion
	    where 1 = 1
	    and ie.flg_activo = 1

        	AND COD_INSTITUCION = #{codInstitucion,jdbcType=NUMERIC}


	        AND COD_PROGRAMA_ACADEMICO = #{codProgramaAcademico,jdbcType=NUMERIC}


	      	AND  UPPER(translate(NOMBRE_PROGRAMA_ACADEMICO, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU'))  like '%'||UPPER(translate(#{nombreProgramaAcademico,jdbcType=VARCHAR}, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU'))||'%'


	      	AND  UPPER(translate(NOMBRE_TITULO_OTORGADO, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU'))  like '%'||UPPER(translate(#{nombreTituloOtorgado,jdbcType=VARCHAR}, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU'))||'%'


	        AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	        AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


	        AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


	        AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


	        AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}

	      order by cod_programa_academico asc

	    select pa.*
	    from PROGRAMA_ACADEMICO pa
	    inner join institucion_Educativa ie on ie.cod_institucion_educativa = pa.cod_institucion
	    where 1 = 1
	    and ie.flg_activo = 1

        	AND COD_INSTITUCION = #{codInstitucion,jdbcType=NUMERIC}


	        AND COD_PROGRAMA_ACADEMICO = #{codProgramaAcademico,jdbcType=NUMERIC}


	      	AND  UPPER(translate(NOMBRE_PROGRAMA_ACADEMICO, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU')) = UPPER(translate(#{nombreProgramaAcademico,jdbcType=VARCHAR}, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU'))


	      	AND  UPPER(translate(NOMBRE_TITULO_OTORGADO, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU')) = UPPER(translate(#{nombreTituloOtorgado,jdbcType=VARCHAR}, 'áéíóúÁÉÍÓÚ', 'aeiouAEIOU'))


	        AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	        AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


	        AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


	        AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


	        AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}

	      order by cod_programa_academico asc
