mapper.select:
	select PU.*, P.NOMBRE_PARAMETRO AS NOMBRE_TIPO_ARTICULO,
		P2.NOMBRE_PARAMETRO AS NOMBRE_TIPO_PUBLICACION,
		P3.NOMBRE_PARAMETRO AS NOMBRE_OTRO_TIPO_PUBLICACION
	from PUBLICACION PU
		LEFT JOIN PARAMETRICA P on PU.COD_TIPO_ARTICULO = P.COD_TABLA_PARAMETRICA
	    LEFT JOIN PARAMETRICA P2 on PU.COD_TIPO_PUBLICACION = P2.COD_TABLA_PARAMETRICA
	    LEFT JOIN PARAMETRICA P3 on PU.COD_OTRO_TIPO_PUBLICACION = P3.COD_TABLA_PARAMETRICA
	 WHERE 1 = 1

        AND PU.COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


        AND COD_TIPO_ARTICULO = #{codTipoArticulo,jdbcType=NUMERIC}


       AND  NOMBRE_ARTICULO = #{nombreArticulo,jdbcType=VARCHAR}


        AND COD_TIPO_PUBLICACION = #{codTipoPublicacion,jdbcType=NUMERIC}


       AND  NOMBRE_LIBRO = #{nombreLibro,jdbcType=VARCHAR}


        AND COD_OTRO_TIPO_PUBLICACION = #{codOtroTipoPublicacion,jdbcType=NUMERIC}


        AND NOMBRE_PUBLICACION = #{nombrePublicacion,jdbcType=VARCHAR}


        AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


        AND AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


        AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


        AND AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


        AND PU.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


        AND FLG_ADMINISTRA_RECURSO = #{flgAdministraRecurso,jdbcType=NUMERIC}
