mapper.select:
     select A.*,
    (SELECT NOMBRE_PAIS FROM PAIS WHERE COD_PAIS = A.COD_PAIS) as NOMBRE_PAIS ,
	(SELECT NOMBRE_DEPARTAMENTO FROM DEPARTAMENTO WHERE COD_DEPARTAMENTO = A.COD_DEPARTAMENTO)as  NOMBRE_DEPARTAMENTO,
	(SELECT NOMBRE_MUNICIPIO FROM MUNICIPIO WHERE COD_MUNICIPIO = A.COD_MUNICIPIO) as  NOMBRE_MUNICIPIO
    from RECONOCIMIENTO A
    where 1 = 1

        AND COD_RECONOCIMIENTO = #{codReconocimiento,jdbcType=NUMERIC}


        AND COD_PERSONA = #{codPersona,jdbcType=NUMERIC}


        AND FLG_ES_PREMIO = #{flgEsPremio,jdbcType=NUMERIC}


        AND FLG_ES_RECONOCIMIENTO = #{flgEsReconocimiento,jdbcType=NUMERIC}


        AND NOMBRE_ENTIDAD = #{nombreEntidad,jdbcType=VARCHAR}


        AND FECHA_RECONOCIMIENTO = #{fechaReconocimiento,jdbcType=TIMESTAMP}


        AND COD_PAIS = #{codPais,jdbcType=NUMERIC}


        AND COD_DEPARTAMENTO = #{codDepartamento,jdbcType=NUMERIC}


        AND COD_MUNICIPIO = #{codMunicipio,jdbcType=NUMERIC}


        AND AUD_FECHA_ACTUALIZACION = #{audFechaActualizacion,jdbcType=TIMESTAMP}


        AUD_COD_USUARIO = #{audCodUsuario,jdbcType=NUMERIC}


        AND AUD_COD_ROL = #{audCodRol,jdbcType=NUMERIC}


        AUD_ACCION = #{audAccion,jdbcType=NUMERIC}


        AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
