mapper.select:
SELECT SA.NOMBRE_SITUACION, SV.*
FROM SITUACION_ADMIN_VINCULACION SV
INNER JOIN SITUACION_ADMINISTRATIVA SA ON SA.COD_SITUACION_ADMINISTRATIVA = SV.COD_SITUACION_ADMINISTRATIVA
WHERE SV.COD_VINCULACION = #{codVinculacion,jdbcType=NUMERIC}		

	and SV.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	and SV.COD_ENTIDAD_PLANTA_DETALLE_ENCARGO = #{codEntidadPlantaDetalleEncargo,jdbcType=NUMERIC}


	and sv.aud_accion != #{audAccion,jdbcType=NUMERIC}


  select sa.*
    from  SITUACION_ADMIN_VINCULACION sa
    inner join  vinculacion v on  v.COD_VINCULACION = sa.COD_VINCULACION
    inner join persona p on  v.COD_PERSONA = p.COD_PERSONA
    where 1=1

		and sa.COD_ENTIDAD_PLANTA_DETALLE_ENCARGO = #{codEntidadPlantaDetalleEncargo,jdbcType=NUMERIC}


		and   sa.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}
		and   v.FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


	 and trunc(sa.fecha_inicio) < = trunc(sysdate) 
	 and trunc(COALESCE(sa.fecha_fin, sysdate)) > = trunc(sysdate)

 	SELECT 

	FROM 
	    SITUACION_ADMIN_VINCULACION
	WHERE 1=1

		AND COD_VINCULACION = #{codVinculacion,jdbcType=NUMERIC}


		AND COD_SITUACION_ADMINISTRATIVA = #{codSituacionAdministrativa,jdbcType=NUMERIC}


		AND COD_ENTIDAD_PLANTA = #{codEntidadPlanta,jdbcType=NUMERIC}


		AND FECHA_INICIO = #{fechaInicio,jdbcType=TIMESTAMP}


		AND FECHA_FIN = #{fechaFin,jdbcType=TIMESTAMP}


		AND COD_TIPO_INCAPACIDAD = #{codTipoIncapacidad,jdbcType=VARCHAR}


		AND COD_TIPO_ACTO_ADMINISTRATIVO = #{codTipoActoAdministrativo,jdbcType=NUMERIC}


		AND NUM_ACTO_ADMINISTRATIVO = #{numActoAdministrativo,jdbcType=VARCHAR}



		AND COD_ENTIDAD_COMISION = #{codEntidadComision,jdbcType=NUMERIC}


		AND DESCRIPCION_ENFERMEDAD = #{descripcionEnfermedad,jdbcType=VARCHAR}


		AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}


		AND COD_TIPO_SUSPENCION = #{codTipoSuspencion,jdbcType=NUMERIC}


		AND DESCRIPCION_ENTIDAD = #{descripcionEntidad,jdbcType=VARCHAR}


		AND DURACION = #{duracion,jdbcType=NUMERIC}


		AND COD_ENTIDAD_PLANTA_DETALLE_ENCARGO = #{codEntidadPlantaDetalleEncargo,jdbcType=NUMERIC}


		AND COD_DEPENDENCIA_ENTIDAD = #{codDependenciaEntidad,jdbcType=NUMERIC}


		AND COD_SA_OCUPA_TEMPORAL = #{codSAOcupaTemporal,jdbcType=NUMERIC}

	ORDER BY AUD_FECHA_ACTUALIZACION DESC
    OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY
