mapper.select:

select 

from SITUACION_ADMINISTRATIVA
where 1 = 1

    AND COD_SITUACION_ADMINISTRATIVA   #{codSituacionAdministrativa,jdbcType=NUMERIC}


     AND UPPER(NOMBRE_SITUACION) LIKE UPPER('%'||#{nombreSituacion,jdbcType=VARCHAR}||'%')


    AND DESCRIPCION LIKE '%'||#{descripcion,jdbcType=VARCHAR}||'%'


    AND FLG_ESGENERICA = #{flgEsgenerica,jdbcType=NUMERIC}


    AND COD_SITUACIONADMIN_PADRE = #{codSituacionadminPadre,jdbcType=NUMERIC}


    AND FLG_ACTIVO = #{flgActivo,jdbcType=NUMERIC}

  ORDER BY NOMBRE_SITUACION ASC

  select *
	from SITUACION_ADMINISTRATIVA sisadmin
	       left outer join ENTIDAD_SITUACION_ADMINISTRATIVA sisent on sisadmin.COD_SITUACION_ADMINISTRATIVA = sisent.COD_SITUACION_ADMINISTRATIVA
	where 1 = 1
	  AND sisent.flg_activo = #{flgActivo,jdbcType=NUMERIC}
	  AND sisadmin.flg_esgenerica = #{flgEsgenerica,jdbcType=NUMERIC}
	  AND sisadmin.flg_activo = #{flgActivo,jdbcType=NUMERIC}
	  AND COD_ENTIDAD = #{codEntidad,jdbcType=NUMERIC}
	order by NOMBRE_SITUACION asc
